import React from 'react'
import { useEffect, useState } from "react";
import Axios from 'axios'
import Restro_Navbar from './Restro_Navbar';


const Dish_Update = (props) => {
    const dish_name=props.location.state;

    const [data, setData] = useState({
        dish:"",
        rest_email:"",
        rest_password:"",
        rest_location:"",
        rest_desc:"",
        

    },);
    function handleSubmit(e){
        const newData={...data};
        newData[e.target.id]=e.target.value;
        setData(newData);
        console.log(newData);

    }
    const updateValue=(e)=>{
        e.preventDefault();
        const url ="http://localhost:3002/updateDish";
            Axios.post(url,{name:sessionStorage.getItem('restro_name'),
            
            dishname:dish_name
            }).then(()=>{
                console.log('success');
            })

    }
    return (
        <div>
        <form >
            <div>
                <h2>Update Dish</h2>
            </div>

            <div>
                <label htmlFor="rest_name"><strong>Dish Name: </strong></label>
                <input onChange={(e)=>handleSubmit(e)} id="rest_name" value={data.dish} type="text" required="required" name="name" placeholder="Enter your name"/> 
            </div>
            <br></br>
            <div>
                <label htmlFor="rest_email"><strong>Dish Price:</strong> </label>
                <input onChange={(e)=>handleSubmit(e)} id="rest_email" value={data.rest_email} type="email" required="required" name="email" placeholder="Enter your email"/>
            </div>
            <br></br>
            <div>
                <label htmlFor="rest_password"><strong>Ingredients: </strong></label>
                <input onChange={(e)=>handleSubmit(e)} id="rest_password" value={data.rest_password} type="password" required="required" name="password" placeholder="Enter your password"/>
            </div>
            <br></br>
            <div>
                <label htmlFor="rest_location"><strong>Description: </strong></label>
                <input onChange={(e)=>handleSubmit(e)} id="rest_location" value={data.rest_location} type="text" required="required" name="rest_location" placeholder="Enter your location"/>
            </div>
            <br></br>
            <div>
                <label htmlFor="rest_desc"><strong>Category:</strong> </label>
                <input onChange={(e)=>handleSubmit(e)} id="rest_desc" value={data.rest_desc} type="text" required="required" name="rest_desc" placeholder="Enter Description"/>
            </div>
            <br></br>
            
           
            <br></br>
            <br></br>
            <div>
                <button className="btn btn-primary sm m-5">submit</button><br></br>
            </div>
            
        </form><br></br>
        
        
    </div>
    )
}

export default Dish_Update
