import React from 'react'
import Navbar from './Navbar';
import {useEffect,useState} from "react";
import Axios from 'axios'

const Checkout = (props) => {
    const[address,setAddress]=useState("");
    const[allAddresses,setAllAddresses]=useState([]);
    // var orderInfo=props.location.newDishArray;
    var orderInfo=JSON.parse(sessionStorage.getItem('cart'));
    useEffect(()=>{
        const url ="http://localhost:3002/getcustaddresses";
        Axios.post(url,{name:sessionStorage.getItem('cust_name')
        },).then((response)=>{
            setAllAddresses(response.data);
        })
    },[])
    const saveAddress = (e)=>{
        e.preventDefault();
        setAllAddresses([address]);
        
            const url ="http://localhost:3002/addaddress";
            Axios.post(url,{name:sessionStorage.getItem('cust_name'),address:address
            },).then(()=>{
                console.log('success');
            })
        
       
    }
    return (
        <div>
            <Navbar/>
            <h3> Order Summary: </h3>
            <h4>------------------------------------------------------</h4>
            {orderInfo.map((val,idx)=>{
                return(
                    <div>
                        
                        <h4>Dish: <strong>{val.Dish_Name}</strong></h4>
                        <h4>Quantity: <strong>{val.quantity}</strong></h4>
                        <h4>Price: <strong>{val.Dish_Price}</strong></h4>
                        <h4>------------------------------------------------------</h4>
                        

                    </div>
                )

            })}
                    <div>
                        <label htmlFor="address:"><strong>Add a new delivery address or choose from exisiting address:-</strong></label><br></br><br></br>
                        <input id="address" name="address" value={address} onChange={
                            (e)=>{
                                setAddress(e.target.value);
                            }
                        } placeholder="Enter delivery address"/> 
                          <button onClick={(e)=>saveAddress(e)
                            }>Add Address</button>
                        <br></br><br></br>
                       
            {allAddresses.map((data)=>{
                return (
                    <div >
                        
                        <input id="address" type="radio" value={data.Cust_Address}  />
                        
                      
                        
                        
                    </div>
                );
                

            })}
                        <button>Place Order</button>
            </div>
        </div>
    )
}

export default Checkout
