import React from 'react'

import Navbar from './Navbar';
import { useEffect, useState } from "react";
import Axios from 'axios'
import { Link } from 'react-router-dom';

const CustomerLanding = () => {

   
    const[RestroInfo,setRestroInfo]=useState([])

    const[CustLocInfo,setCustLocInfo]=useState([])
    const[search,setSearch]=useState("")
    
    useEffect(()=>{
        const url ="http://localhost:3002/getCustLocation";
        Axios.post(url,{name:sessionStorage.getItem('cust_name')
        },).then((response)=>{
            setCustLocInfo(response.data);
        })
    },[])
    

    var cust_location;
    CustLocInfo.map((val)=>{
        cust_location=val.City;

    })
    sessionStorage.setItem('cust_location',cust_location);
    
    useEffect(()=>{
        const url ="http://localhost:3002/getAllRestaurants";
        Axios.get(url).then((response)=>{
            setRestroInfo(response.data);
        })
    },[])
    var custLocRestro=RestroInfo.filter(val=>val.Rest_Location===sessionStorage.getItem('cust_location'));
    var notCustLocRestro=RestroInfo.filter(val=>val.Rest_Location!=sessionStorage.getItem('cust_location'));

    custLocRestro.push(...notCustLocRestro);

    var restroMap = new Map();
    var restroDishes=[];

    function getRestro(restroNm){
                
            const url ="http://localhost:3002/getDishesByRestro";
            Axios.post(url,{name:restroNm
            },).then((response)=>{
                //setRestroDishes(response.data);
                restroDishes.push(response.data);

                
            })
        
         restroMap.set(restroNm,restroDishes)
         restroDishes.splice(0, restroDishes.length)
    }
    custLocRestro.forEach((val)=>{
            getRestro(val.Rest_Name);
      
        
    })
 
    return (
        <div>
            
            <Navbar sendSearch={word=>{setSearch(word)}}/>
            {/* <h1>{search}</h1> */}
           {custLocRestro.filter((val)=>{
               if(search==""){
                   return val;
               }
               else if(val.Rest_Name.toLowerCase().includes(search.toLowerCase())){
                    return val;
               }
               else if(val.Rest_Location.toLowerCase().includes(search.toLowerCase())){
                return val;
           }
        //    var tempdishes = restroMap.get(val);
        //    tempdishes.filter((val1)=>{
        //        if(val1.toLowerCase().includes(search.toLowerCase())){
        //            return val;
        //        }

        //    })
               
           }).map
           ((val)=>{
            return (
                <div >
                    <img src={val.Rest_Image} />
                    <h3><strong>Name : </strong>
                    <Link  to={{
                            pathname: "/viewrestro",
                            state:{val}
                            }}><strong>{val.Rest_Name}</strong></Link>
                    </h3>
                    
                    <h3><strong>Description : </strong><strong>{val.Rest_Desc}</strong></h3>
                    
                    <h3><strong>Location : </strong><strong>{val.Rest_Location}</strong></h3>
                    <h3>----------------------------------------------------</h3>
                    
                    
                  
                </div>
            );
            

        })}
  
        

            
        </div>
    )
}

export default CustomerLanding
