import React from 'react'
import {useState} from "react";
import {Link,useHistory } from 'react-router-dom';
import '../App.css'
import Axios from 'axios'


const Customer_Login = () => {
    let history=useHistory();
    
    
    const [data, setData] = useState({
        custname:"",
        password:""
     
    });
    const[CustLocInfo,setCustLocInfo]=useState([])
    sessionStorage.setItem('cust_name',data.custname)

    const[error,setError]=useState("");

    function handleSubmit(e){
        const newData={...data};
        newData[e.target.id]=e.target.value;
        setData(newData);
    

    }
    const Login=(userName,e)=>{
        e.preventDefault();
        
        const url="http://localhost:3002/login"
        Axios.post(url,{name:userName
            },).then((response)=>{
                console.log(response.data[0].password);
               // res = response.data;
               // console.log(res);
                if(response.data[0].password===data.password){
                    console.log('inside');
                    history.push("/customerLanding");
                }
                else{
                    setError('Invalid Credentials');
                }
               
            })

    }
    return (
        <div>
            <div style={{width: "100%",float: "center",color:'red'}}>
                <h3>{error}</h3>
            </div><br></br>
            <form >
                <div>
                    <h2><strong>Customer Login</strong></h2>
                </div>

                <div className="login">
                    <label htmlFor="custname"><strong>Customer Name:</strong> </label>
                    <input  id="custname" onChange={(e)=>handleSubmit(e)}  type="text" value={data.custname} required="required" name="custname" value={data.custname} placeholder="Enter your name"/> 
                
                <br></br>
                
                
                    <label htmlFor="password"><strong>Customer Password:</strong> </label>
                    <input id="password" type="password" onChange={(e)=>handleSubmit(e)} value={data.password} required="required" name="password" placeholder="Enter your password"/>
                </div>
                <br></br>
                <div>
                    <button onClick={(e)=>Login(data.custname,e)} className="btn btn-primary sm m-5">Login</button>
                </div>
            </form><br></br>
            <ul>
        <li><Link to="/customerSignup"><strong>Don't have an account? SignUp</strong></Link></li> <br></br>
        
      </ul>
      
      
            
            
        </div>
    )
}

export default Customer_Login
