import React from 'react'
import Navbar from './Navbar'
import {useEffect, useState } from "react";
import Axios from 'axios'
import Table from 'react-bootstrap/Table'

import '../App.css'

const Order = () => {
    const cust_name=sessionStorage.getItem('cust_name')
    const[orderInfo,setOrderInfo]=useState([]);
    const [orderDetails,setOrderDetails]=useState([]);
    const[orderid,setOrderId] = useState(0);
    const[radio,setRadio]=useState("");

    useEffect(()=>{
        const url ="http://localhost:3002/getOrderDetails";
        Axios.post(url,{cust_name:cust_name
        },).then((response)=>{
            setOrderInfo(response.data);
        })
    },[])

    
        useEffect(()=>{
            const url ="http://localhost:3002/viewOrderDetails";
            Axios.get(url).then((response)=>{
                setOrderDetails(response.data);
            })
        },[])

        var order_ID=0;
        const viewDetails=(id)=>{
            setOrderId(id);
            console.log(order_ID)
        }


    
    return (
        <div>
            <Navbar/>
            <h3> Orders Summary..........</h3> <br></br>

            <div className="radio__filter">
                <label htmlFor="neworder">New Order </label>
                <input id="neworder" type="radio" value="order placed" checked={radio==="order placed"} onChange={(e)=>setRadio(e.target.value)}/>
                <label htmlFor="delivered">Delivered</label>
                <input id="delivered" type="radio" value="delivered" checked={radio==="delivered"} onChange={(e)=>setRadio(e.target.value)}/>
                
                <button onClick={()=>setRadio("")}>Reset</button>

            </div> <br></br>
            
            <div className="table">
            <Table >
                <thead>
                    <tr>
                        
                        <th>Restaurant Name</th>
                        <th>Order Status</th>
                        <th>Order Date</th>
                        <th>Order Time</th>
                        <th>Order Details</th>
                    </tr>
                </thead>
                <tbody>
                {orderInfo.map((val)=>{
                return(
                    <tr>
                        <td>{val.Restro_Name}</td>
                        <td>{val.Order_Status}</td>
                        <td>{val.Order_Dt}</td>
                        <td>{val.Order_Time}</td>
                        <td><button onClick={()=>viewDetails(val.Order_Id)}>View Details</button></td>
                        

                    </tr>
                )
            })}
                </tbody>
            </Table>
            </div>

            {orderDetails.filter((value)=>{
                if(value.Order_Id==orderid){
                    return value;
                }
            }).
            map((value)=>{
                return(
                    <div>
                        <h4>Dish : {value.Order_Dish}</h4>
                        <h4>Quantity: {value.Quantity}</h4>
                        

                    </div>
                )
            })}
            
        </div>
    )
}

export default Order
