import React from 'react'
import {useEffect, useState } from "react";
import Axios from 'axios'
import Table from 'react-bootstrap/Table'

import '../App.css'
import Restro_Navbar from './Restro_Navbar';

const Restro_Order = () => {
    const restro_name=sessionStorage.getItem('restro_name')
    const[orderInfo,setOrderInfo]=useState([]);

    useEffect(()=>{
        const url ="http://localhost:3002/getRestroOrderDetails";
        Axios.post(url,{restro_name:restro_name
        },).then((response)=>{
            setOrderInfo(response.data);
        })
    },[])

    return (
        
        <div>
            <Restro_Navbar/>
            <h3> Orders Summary..........</h3> <br></br>
            <div className="table">
            <Table >
                <thead>
                    <tr>
                        
                        <th>Customer Name</th>
                        <th>Order Date</th>
                        <th>Order Time</th>
                        <th>Order Status</th>
                        <th>Order Details</th>
                    </tr>
                </thead>
                <tbody>
                {orderInfo.map((val)=>{
                return(
                    <tr>
                        <td>{val.Cust_Name}</td>
                        <td>{val.Order_Dt}</td>
                        <td>{val.Order_Time}</td>
                        <td>{val.Order_Status}</td>
                        <td><button>View Details</button></td>
                        

                    </tr>
                )
            })}
                </tbody>
            </Table>
            </div>
            
        </div>
    )
}

export default Restro_Order
