import React from 'react'
import Navbar from './Navbar'
import {useEffect, useState } from "react";
import Axios from 'axios'
import Table from 'react-bootstrap/Table'
//import 'bootstrap/dist/css/bootstrap.css';
import 'react-bootstrap-table/css/react-bootstrap-table.css';
const Order = () => {
    const cust_name=sessionStorage.getItem('cust_name')
    const[orderInfo,setOrderInfo]=useState([]);

    useEffect(()=>{
        const url ="http://localhost:3002/getOrderDetails";
        Axios.post(url,{cust_name:cust_name
        },).then((response)=>{
            setOrderInfo(response.data);
        })
    },[])
    return (
        <div>
            <Navbar/>
            <h3> Orders Summary..........</h3> <br></br>
            <Table >
                <thead>
                    <tr>
                        
                        <th>Restaurant Name</th>
                        <th>Order Status</th>
                        <th>Order Details</th>
                    </tr>
                </thead>
                <tbody>
                {orderInfo.map((val)=>{
                return(
                    <tr>
                        <td>{val.Restro_Name}</td>
                        <td>{val.Order_Status}</td>
                        <td><button>View Details</button></td>
                        

                    </tr>
                )
            })}
                </tbody>
            </Table>

            {/* {orderInfo.map((val)=>{
                return(
                    <div>
                        <h3>Restaurant Name : {val.Restro_Name}</h3>
                        <h3>Order Status : {val.Order_Status}</h3>
                        <button>View Details</button>
                        <h3>------------------------------------------</h3>

                    </div>
                )
            })} */}
            
        </div>
    )
}

export default Order
