import React from 'react'
import { useEffect, useState } from "react";
import Axios from 'axios'
import Restro_Navbar from './Restro_Navbar';


const Dish_Update = (props) => {
    const dish_name=props.location.state;

    const[updateDish,setUpdateDish]=useState({
        name:"",
        price:"",
        description:"",
        ingredients:"",
        category:""
    }
    );
    function handleSubmit(e){
        const newData={...updateDish};
        newData[e.target.id]=e.target.value;
        setUpdateDish(newData);
        console.log(newData);

    }
    const updateValue=(e)=>{
        e.preventDefault();
        const url ="http://localhost:3002/updateDish";
            Axios.post(url,{name:sessionStorage.getItem('restro_name'),
            data:updateDish.price,
            dishname:dish_name
            }).then(()=>{
                console.log('success');
            })

    }
    return (
                    <div>
                        <Restro_Navbar/><br></br><br></br>
                        <form>
                        <div>
                             <label htmlFor="dish_name"><strong>Dish Name: </strong></label>
                             <input  id="dish_name" onChange={(e)=>handleSubmit(e)}  type="text" name="dish_name" placeholder="Enter dish name"/> 
                         </div>
                         <div>
                             <label htmlFor="dish_price"><strong>Dish Price: </strong></label>
                             <input onChange={(e)=>handleSubmit(e)} id="dish_price" value={updateDish.price} type="text" name="dish_price" placeholder="Enter dish price"/>
                         </div>
                         <div>
                             <label htmlFor="dish_description"><strong>Dish Description: </strong></label>
                             <input  onChange={(e)=>handleSubmit(e)} id="dish_description" value={updateDish.description} type="text"  name="dish_description" placeholder="Enter dish description"/>
                         </div>
                         <div>
                             <label htmlFor="dish_ingredients"><strong>Main Ingredients: </strong></label>
                             <input onChange={(e)=>handleSubmit(e)} id="dish_ingredients" value={updateDish.ingredients} type="text"  name="dish_ingredients" placeholder="Enter dish ingredients"/>
                          </div>
                          <div>
                            <label htmlFor="dish_category"><strong>Dish Category: </strong></label>
                            <input onChange={(e)=>handleSubmit(e)} id="dish_category" value={updateDish.category} type="text" name="dish_category" placeholder="Enter dish category"/><br></br>
                          </div>
                          <div>
                            <button onClick={(e)=>updateValue(e)}> Update </button>
                          </div>
                          </form>
            
        </div>
    )
}

export default Dish_Update
