import React from 'react'
import { useEffect, useState } from "react";
import Axios from 'axios'
import Restro_Navbar from './Restro_Navbar';

const Restro_Dish = () => {
    const[dishInfo,setDishInfo]=useState([]);
    const[price,setPrice]=useState(0);

    useEffect(()=>{
        const url ="http://localhost:3002/getDishesByRestro";
        Axios.post(url,{name:sessionStorage.getItem('restro_name')
        },).then((response)=>{
            setDishInfo(response.data);
        })
    },[])

    return (
        <div>
            <Restro_Navbar/>
            <h3>Our menu - </h3>
            {dishInfo.map((val,idx)=>{
                return (
                    // <div key={idx}>
                    //     <br></br><img src={val.dish_image} />
                    //     <h3><strong>Dish : </strong><strong>{val.Dish_Name}</strong></h3>
                        
                    //     <h3><strong>Price : </strong><strong>{val.Dish_Price}</strong></h3>
                    
                        
                    //     <h3><strong>Description : </strong><strong>{val.Description}</strong></h3>

                    //     <h3><strong>Ingredients : </strong><strong>{val.Main_Ingredients}</strong></h3>

                    //     <h3><strong>Category : </strong><strong>{val.Dish_Category}</strong></h3> <br></br>

                    //     <button>Update</button>
                        
                        
                    // </div>
                    <div key={idx}>
                         <br></br><img src={val.dish_image} />
                         <div>
                             <label htmlFor="dish_name"><strong>Dish Name: </strong></label>
                            <input  id="dish_name" value={val.Dish_Name} type="text" required="required" name="dish_name" placeholder="Enter dish name"/> 
                        </div>
                        <div>
                            <label htmlFor="dish_price"><strong>Dish Price: </strong></label>
                            <input onChange={(e)=>setPrice(e.target.value)} id="dish_price" value={val.Dish_Price} type="text" required="required" name="dish_price" placeholder="Enter dish price"/>
                        </div>
                        <div>
                            <label htmlFor="dish_description"><strong>Dish Description: </strong></label>
                            <input  id="dish_description" value={val.Description} type="text" required="required" name="dish_description" placeholder="Enter dish description"/>
                        </div>
                        <div>
                            <label htmlFor="dish_ingredients"><strong>Main Ingredients: </strong></label>
                            <input  id="dish_ingredients" value={val.Main_Ingredients} type="text" required="required" name="dish_ingredients" placeholder="Enter dish ingredients"/>
                         </div>
                         <div>
                         <label htmlFor="dish_category"><strong>Dish Category: </strong></label>
                         <input  id="dish_category" value={val.Dish_Category} type="text" required="required" name="dish_category" placeholder="Enter dish category"/><br></br>
                         </div>
                         <div>
                         <button> Update </button>
                         </div>
                        </div> 
                        
                );
                

            })}
            
        </div>
    )
}

export default Restro_Dish
