import React from 'react'
import { Link,useHistory } from 'react-router-dom';

import {useState } from "react";

import Axios from 'axios'


const Restro_Login = () => {
    let history=useHistory();
    const[error,setError]=useState("");
    const [state, setState] = useState({
        name: "",
        password:""
        });
    sessionStorage.setItem('restro_name',state.name);
    
    
    function handleSubmit(e){
        const newData={...state};
        newData[e.target.id]=e.target.value;
        setState(newData);
        //console.log(newData);
    
        }

        const Login=(userName,e)=>{
            e.preventDefault();
            
            const url="http://localhost:3002/restroLogin"
            Axios.post(url,{name:userName
                },).then((response)=>{
                    console.log(response.data[0].password);
                   // res = response.data;
                   // console.log(res);
                    if(response.data[0].Rest_Password===state.password){
                        console.log('inside');
                        history.push("/restroLanding");
                    }
                    else{
                        setError('Invalid Credentials');
                    }
                   
                })
    
        }
    
    return (
        <div>
            <form >
            <div style={{width: "105%",float: "center",color:'red'}}>
                <h3>{error}</h3>
            </div>
                <div>
                    <h2>Restaurant Login</h2>
                </div>

                <div style={{color:'black'}}>
                    <label htmlFor="name"><strong>Restaurant Name:</strong> </label>
                    <input id="name" onChange={(e)=>handleSubmit(e)} type="text" value={state.name} required="required" name="name" placeholder="Enter your name"/> 
                </div>
                <br></br>
                
                <div>
                    <label htmlFor="password"><strong>Restaurant Password:</strong></label>
                    <input id="password" type="password" required="required" onChange={(e)=>handleSubmit(e)} value={state.password} name="password" placeholder="Enter your password"/>
                </div>
                <br></br>
                <div>
                    <button onClick={(e)=>Login(state.name,e)} className="btn btn-primary sm m-5">Login</button>
                </div>
            </form><br></br>
            <ul>
        <li><Link to="/restaurantSignup"><strong>Don't have an account? SignUp</strong></Link></li> <br></br>
        
      </ul>
      
            
        </div>
    )
}

export default Restro_Login
