import React from 'react'
import { Router, Route, Link, browserHistory, IndexRoute,useHistory,Redirect } from 'react-router-dom';
import Restro_Signup from './Restro_Signup';
import { useEffect, useState } from "react";
import Axios from 'axios'
import CustomerLanding from './CustomerLanding';
import Restro_landing from './Restro_landing';



const Restro_Login = () => {
    let history=useHistory();
    
    const [state, setState] = useState({
        name: ""
        });
    sessionStorage.setItem('restro_name',state.name);
    
    function handleSubmit(e){
        const newData={...state};
        newData[e.target.id]=e.target.value;
        setState(newData);
        //console.log(newData);
    
        }
    
    return (
        <div>
            <form >
                <div>
                    <h2>Restaurant Login</h2>
                </div>

                <div style={{color:'black'}}>
                    <label htmlFor="name"><strong>Restaurant Name:</strong> </label>
                    <input id="name" onChange={(e)=>handleSubmit(e)} type="text" required="required" name="name" placeholder="Enter your name"/> 
                </div>
                <br></br>
                
                <div>
                    <label htmlFor="password"><strong>Restaurant Password:</strong></label>
                    <input id="password" type="password" required="required" name="password" placeholder="Enter your password"/>
                </div>
                <br></br>
                <div>
                    {/* <button onClick={()=>{
                        

                         history.push("/restroLanding");
                    
                
                }}className="btn btn-primary sm m-5">Login</button> */}
                        <Link
                            className="btn btn-primary"
                            to={{
                            pathname: "/restroLanding",
                            state
                            }}
                        ><button> Login </button>
                            {/* Login */}
                        </Link>
                </div>
            </form><br></br>
            <ul>
        <li><Link to="/restaurantSignup"><strong>Don't have an account? SignUp</strong></Link></li> <br></br>
        
      </ul>
      
            
        </div>
    )
}

export default Restro_Login
