import React,{useState} from 'react'
import Navbar from './Navbar'


const Cart = props => {
    const dishArray=props.location.state.cart;
    const[newDishArray,setNewDishArray]=useState(dishArray);

    const additem=(val)=>{
        const ifExist=newDishArray.find(dish=>dish.Dish_Name===val.Dish_Name);
        if(ifExist){
            const nextState = newDishArray.map((item)=>item.Dish_Name===val.Dish_Name?{
                ...ifExist,quantity:ifExist.quantity+1
            }:item)
            setNewDishArray(nextState);
        }
    }
    const removeitem=(val)=>{
        const ifExist=newDishArray.find(dish=>dish.Dish_Name===val.Dish_Name);
        if(ifExist){
            const nextState = newDishArray.map((item)=>(item.Dish_Name===val.Dish_Name && item.quantity>0)?{
                ...ifExist,quantity:ifExist.quantity-1
            }:item)
            setNewDishArray(nextState);
            const updatedArray=newDishArray.filter(val=>val.quantity>0);
            setNewDishArray(updatedArray);
            {newDishArray.length===0 && <div> Cart is Empty </div>}
        }
    }
    return (
        <div>
            <Navbar/>
            <h3>Made it to cart!</h3>
            {newDishArray.length===0 && <div> Cart is Empty </div>}
            {newDishArray.filter(val=>val.quantity>0).map((val,idx)=>{
            return (
                <div >
                    

                    
                    <h3><strong>Dish Name : </strong><strong>{val.Dish_Name}</strong></h3>
                    
                    <h3><strong>Price : </strong><strong>{val.Dish_Price}</strong></h3>
                    <h3><strong>Quantity : </strong><strong>{val.quantity}</strong></h3>
                    <button onClick={()=>additem(val)}>Add</button><br></br>
                    <button onClick={()=>removeitem(val)}>Remove</button>
                    
                    <h3>----------------------------------------------------</h3>
                    
                    
                  
                </div>
            );
            

        })}
        </div>
    )
}
 
export default Cart
