import React from 'react'
import {useEffect, useState } from "react";
import Axios from 'axios'
import Table from 'react-bootstrap/Table'
import { Link } from 'react-router-dom';

import '../App.css'
import Restro_Navbar from './Restro_Navbar';

const Restro_Order = () => {
    const restro_name=sessionStorage.getItem('restro_name')
    const[orderInfo,setOrderInfo]=useState([]);
    const[dropDown,setDropDown]=useState("");
    const[radio,setRadio]=useState("");

    useEffect(()=>{
        const url ="http://localhost:3002/getRestroOrderDetails";
        Axios.post(url,{restro_name:restro_name
        },).then((response)=>{
            setOrderInfo(response.data);
        })
    },[])

    const updateStatus=(dropdown,id)=>{
        const url ="http://localhost:3002/updateOrderStatus";
        Axios.post(url,{value:dropdown,
            order_id:id
        },).then(()=>{
            console.log('success');
            window.location.reload();
        })

    }

    return (
        
        <div>
            <Restro_Navbar/>
            <h3> Orders Summary..........</h3> <br></br>
            <div className="radio__filter">
                <label htmlFor="neworder">New Order </label>
                <input id="neworder" type="radio" value="order placed" checked={radio==="order placed"} onChange={(e)=>setRadio(e.target.value)}/>
                <label htmlFor="delivered">Delivered</label>
                <input id="delivered" type="radio" value="delivered" checked={radio==="delivered"} onChange={(e)=>setRadio(e.target.value)}/>
                
                <button onClick={()=>setRadio("")}>Reset</button>

            </div> <br></br>
            <div className="table">
            <Table >
                <thead>
                    <tr>
                        
                        <th>Customer Name</th>
                        <th>Order Date</th>
                        <th>Order Time</th>
                        <th>Order Status</th>
                        <th>Update Status</th>
                        <th>Order Details</th>
                        <th>Save</th>
                    </tr>
                </thead>
                <tbody>
                {orderInfo.filter((data)=>{
                    if(radio=="order placed"){
                        if(val.Order_Status=="Order Placed"){
                            return data;
                        }

                    }
                    else if(radio=="delivered"){
                        if(val.Order_Status=="Delivered"){
                            return data;
                        }

                    }
                })
                .map((val)=>{
                return(
                    <tr>
                        <Link to={{
                            pathname:"/restrocustprofile",
                            state:val.Cust_Name
                        }
                        
                        }><td>{val.Cust_Name}</td></Link>
                        <td>{val.Order_Dt}</td>
                        <td>{val.Order_Time}</td>
                        <td>{val.Order_Status}</td>
                        <td>
                        <select name="status" id="status" onChange={(e)=>setDropDown(e.target.value)} value={dropDown}>
                            <option value="select" selected>Select</option>
                            <option value="In the Kitchen">In the Kitchen</option>
                            <option value="On the way">On the way</option>
                            <option value="Delivered">Delivered</option>
                        </select>
                        </td>
                        <td><button>View Details</button></td>
                        <td><button onClick={()=>updateStatus(dropDown,val.Order_Id)}>Save</button></td>

                    </tr>
                )
            })}
                </tbody>
            </Table>
            </div>
            
        </div>
    )
}

export default Restro_Order
