import React from 'react'
import {useState,useEffect } from "react";
import { Router, Route, Link, browserHistory, IndexRoute,useHistory } from 'react-router-dom';
import Signup from './Signup';
import Axios from 'axios'

const Customer_Login = () => {
    let history=useHistory();
    
    const [data, setData] = useState({
        custname:""
     
    });
    const[CustLocInfo,setCustLocInfo]=useState([])
    sessionStorage.setItem('cust_name',data.custname)
    useEffect(()=>{
        const url ="http://localhost:3002/getCustLocation";
        Axios.post(url,{name:data.custname
        },).then((response)=>{
            setCustLocInfo(response.data);
        })
    },[])
    var cust_location;
    CustLocInfo.map((val)=>{
        cust_location=val.City;
        sessionStorage.setItem('cust_loc',cust_location)

    })
    function handleSubmit(e){
        const newData={...data};
        newData[e.target.id]=e.target.value;
        setData(newData);
    

    }
    return (
        <div>
            <form >
                <div>
                    <h2><strong>Customer Login</strong></h2>
                </div>

                <div>
                    <label htmlFor="custname"><strong>Customer Name:</strong> </label>
                    <input id="custname" onChange={(e)=>handleSubmit(e)}  type="text" required="required" name="custname" value={data.custname} placeholder="Enter your name"/> 
                </div>
                <br></br>
                
                <div>
                    <label htmlFor="password"><strong>Customer Password:</strong> </label>
                    <input id="password" type="password" required="required" name="password" placeholder="Enter your password"/>
                </div>
                <br></br>
                <div>
                    <button onClick={()=>{history.push("/customerLanding")}} className="btn btn-primary sm m-5">Login</button>
                </div>
            </form><br></br>
            <ul>
        <li><Link to="/customerSignup"><strong>Don't have an account? SignUp</strong></Link></li> <br></br>
        
      </ul>
      
      
            
            
        </div>
    )
}

export default Customer_Login
